import Foundation
// Error handling not used becaused data is generated by ourself
enum dateGenerateError: Error {
    case invalidUrl
    case errorDecode
    case failed(error: Error)
    case unknownError
}

struct DateManager {
    static let shared = DateManager()
    
    func getDate(perPage: Int = 30, sinceDate: Int) -> [selfDefinedDate] {
        // 2
        var Result:[selfDefinedDate] = []
        for i in sinceDate..<(sinceDate + perPage) {
            Result.append(selfDefinedDate(year: i))
        }
        return Result
    }
}
